
//#region src/functions.ts
/** 安全判断对象是否为特定类实例 */
function isInstanceOf(obj, constructor) {
	return typeof obj === "object" && obj instanceof constructor;
}
/** 安全转换对象为特定类实例 */
function asInstanceOf(obj, constructor) {
	return isInstanceOf(obj, constructor) ? obj : void 0;
}
/** 运行时删除对象指定属性（浅拷贝） */
function omit(t, keys) {
	const clone = { ...t };
	for (const key of keys) delete clone[key];
	return clone;
}

//#endregion
exports.asInstanceOf = asInstanceOf;
exports.isInstanceOf = isInstanceOf;
exports.omit = omit;